import React from "react";
import "./App.css";
import Navbar from "./components/Navbar";
import Textform from "./components/Textform";
import { useState } from "react";
import Alert from "./components/Alert";
import About from "./components/About";
import { createRoot } from "react-dom/client";
import {
  createBrowserRouter,
  RouterProvider,
  Route,
  Link,
} from "react-router-dom";

function App() {
  const [mode, setMode] = useState("light");
  const [alert, setAlert] = useState(null)

  const showAlert=(message,type)=>{
        setAlert({
          message:message,
          type:type
        })
        setTimeout(() => {
          setAlert(null)
        }, 1500);
  }

  const toggleMode = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundColor = "midnightblue";
      showAlert('darkmode has been enabled','sucess')
      document.title='harsh text analyzer-darkmode'
    }
     else {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert('lightmode has been enabled','sucess')
      document.title='harsh text analyzer-lightmode'
    }
  };

  return (
    <>
    <router>
      <Navbar title="harsh text analyzer"  mode={mode} toggleMode={toggleMode} />
    <Alert alert={alert}/>
      <div className="container my-3">
        {/* <Textform showAlert={showAlert} heading="enter text to analyze" mode={mode} /> */}
        <About/>
      </div>
      </router>
    </>
  );
}
export default App;
